package javaprograms.Arrays1;

public class Swap_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=20;
		System.out.println("Before swap a="+a+" b="+b);
		int temp=a;
		a=b;
		b=temp;
		System.out.println("after swap a="+a+" b="+b);

	}

}
